import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router'; // Import Router

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  name = '';
  surname = '';

  constructor(private http: HttpClient, private router: Router) {} // Inject Router

  onSubmit() {
    const formData = { name: this.name, surname: this.surname };
    this.http.post('http://localhost:3000/api/submit', formData).subscribe(
      (response) => {
        console.log('Data sent to server:', response);
        // Clear the form fields after successful submission
        this.name = '';
        this.surname = '';
        // Navigate to the success page
        this.router.navigate(['/success']);
      },
      (error) => {
        console.error('Error sending data:', error);
        // Handle error
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Angular Form Example</title>
</head>
<body>
  <div>
    <h2>Angular Form Example</h2>
    <form (ngSubmit)="onSubmit()">
      <label for="name">Name:</label>
      <input type="text" id="name" [(ngModel)]="name" name="name" required>

      <label for="surname">Surname:</label>
      <input type="text" id="surname" [(ngModel)]="surname" name="surname" required>

      <button type="submit">Submit</button>
    </form>
  </div>
  <router-outlet></router-outlet> <!-- This outlet is for displaying routed components -->
</body>
</html>
